import React, { Component } from 'react'

export class Bezitting extends Component {


    getStyle = () => {
        if (this.props.items=== 1){
            return{
                background: 'blue',
                textDecoration: 'none'
            }
        }else{
            return{
                background: 'red',
                textDecoration: 'line-through'

            }

        }
    }
   
    render() {
        return (
            this.props.items.map((item) => 
            <div key= {item.id} style={this.getStyle()} >
                 <p>{item.id}</p>
                 <p >{item.bezitting}</p>
                 <p>{item.waarde}</p>
            </div>
            )
        )
    }
}


export default Bezitting
