import React, { Component } from 'react'

export class Bezitting extends Component {

/*
    getStyle(id) {
        if(id === 1){
            return{
                background: 'red'
            }
        }else{
            return{
                background: 'blue'
            }
        }
    }
    */

    getStyle = () => {
        return{
           background: this.props.items.id === 3 ? 'grey' : 'blue'
        }
    } 

    
   
    render() {
        return this.props.items.map((item) => (
            <div>
                <p style = {this.getStyle(item.id)}>{item.id}</p>
                <p>{item.bezitting}</p>
                <p>{item.waarde}</p>
            </div>
        ));
    }
}


export default Bezitting
